import React from "react";
export default class Search extends React.Component {
  state = {
    search: "",
    Type: "all",
  };
  key = (e) => {
    if (e.key === "Enter") {
      this.props.searchMovies(this.state.search);
    }
  };
  filter = (e) => {};

  render() {
    return (
      <div className="row">
        <div className="input-field col s12" />
        <input
          type="search"
          className="validate"
          placeholder="search"
          value={this.state.search}
          onChange={(event) => this.setState({ search: event.target.value })}
          onKeyDown={this.key}
        />
        <button
          className="btn"
          onClick={() => this.props.searchMovies(this.state.search)}
        >
          Search
        </button>
        <div className="filters">
          <p>
            <label>
              <input type="checkbox" name="type" datatype="movie" />
              <span>Movie</span>
            </label>
          </p>
          <p>
            <label>
              <input type="checkbox" name="type" datatype="series" />
              <span>Series</span>
            </label>
          </p>
          <p>
            <label>
              <input type="checkbox" name="type" datatype="episode" />
              <span>Episode</span>
            </label>
          </p>
        </div>
      </div>
    );
  }
}
